// Handle empty or malformed JSONBin data
const rawData = $json;
const sessionId = $node['Extract Message Data'].json['sessionId'] || 'unknown';
const message = $node['Extract Message Data'].json['message'] || '';

// Initialize default structure if bin is empty or malformed
let binData = {
  conversations: {},
  stats: {
    totalSessions: 0,
    lastUpdated: new Date().toISOString()
  }
};

// Try to get the actual data from JSONBin response
if (rawData && rawData.record) {
  // JSONBin wraps data in 'record' property
  binData = rawData.record;
} else if (rawData && rawData.conversations) {
  // Direct data structure
  binData = rawData;
}

// Ensure conversations object exists
if (!binData.conversations) {
  binData.conversations = {};
}

// Ensure stats object exists
if (!binData.stats) {
  binData.stats = {
    totalSessions: 0,
    lastUpdated: new Date().toISOString()
  };
}

// Build session key
const sessionKey = 'session_' + sessionId;

// Get conversation history (empty array if not exists)
const conversationHistory = binData.conversations[sessionKey]?.messages || [];

// Get existing conversation data
const existingConversation = binData.conversations[sessionKey] || null;

return {
  json: {
    conversationHistory: conversationHistory,
    currentMessage: message,
    sessionKey: sessionKey,
    fullBinData: JSON.stringify(binData),
    existingConversation: existingConversation,
    binDataObject: binData
  }
};

// Parse AI intent classification and create routing logic
const aiResponse = $json.choices[0].message.content;
let classification;

try {
  classification = JSON.parse(aiResponse);
} catch (e) {
  // Fallback classification if parsing fails
  classification = {
    primary_intent: 'general_conversation',
    lead_quality: 'cold',
    urgency_level: 'exploring',
    confidence_score: 0.5,
    next_best_action: 'continue_conversation'
  };
}

// Determine routing paths based on classification
const isHighIntentLead = 
  (classification.primary_intent === 'demo_request' || 
   classification.primary_intent === 'pricing_inquiry' ||
   classification.primary_intent === 'scheduling') &&
  (classification.lead_quality === 'hot' || classification.lead_quality === 'warm') &&
  classification.confidence_score > 0.6;

const shouldRequestContact = 
  classification.next_best_action === 'request_contact_info' ||
  classification.next_best_action === 'schedule_demo' ||
  (classification.lead_quality === 'hot' && classification.urgency_level === 'immediate');

// Return classification data with routing flags
return {
  json: {
    ...classification,
    // Routing flags
    isHighIntentLead: isHighIntentLead,
    shouldRequestContact: shouldRequestContact,
    useLeadFlow: isHighIntentLead || shouldRequestContact,
    // Conversation context for enhanced prompts
    conversationContext: {
      stage: classification.conversation_stage || 'discovery',
      interests: (classification.specific_interests || []).join(', '),
      businessContext: classification.business_context || 'unknown',
      urgency: classification.urgency_level || 'exploring'
    }
  }
};

