// Handle empty or malformed JSONBin data
const rawData = $json;
const sessionId = $node['Extract Message Data'].json['sessionId'] || 'unknown';
const message = $node['Extract Message Data'].json['message'] || '';

// Initialize default structure if bin is empty or malformed
let binData = {
  conversations: {},
  stats: {
    totalSessions: 0,
    lastUpdated: new Date().toISOString()
  }
};

// Try to get the actual data from JSONBin response
if (rawData && rawData.record) {
  // JSONBin wraps data in 'record' property
  binData = rawData.record;
} else if (rawData && rawData.conversations) {
  // Direct data structure
  binData = rawData;
}

// Ensure conversations object exists
if (!binData.conversations) {
  binData.conversations = {};
}

// Ensure stats object exists
if (!binData.stats) {
  binData.stats = {
    totalSessions: 0,
    lastUpdated: new Date().toISOString()
  };
}

// Build session key
const sessionKey = 'session_' + sessionId;

// Get conversation history (empty array if not exists)
const conversationHistory = binData.conversations[sessionKey]?.messages || [];

// Get existing conversation data
const existingConversation = binData.conversations[sessionKey] || null;

return {
  json: {
    conversationHistory: conversationHistory,
    currentMessage: message,
    sessionKey: sessionKey,
    fullBinData: JSON.stringify(binData),
    existingConversation: existingConversation,
    binDataObject: binData
  }
};

const binData = JSON.parse($node['Extract Conversation Data'].json['fullBinData'] || '{}');
const sessionKey = $node['Extract Conversation Data'].json['sessionKey'];
const sessionId = $node['Extract Message Data'].json['sessionId'];
const userId = $node['Extract Message Data'].json['userId'];
const updatedMessages = $node['Prepare Standard Response'].json['updatedMessages'];

