    contactProvided: binData.conversations[sessionKey]?.metadata?.contactProvided || false,
    demoRequested: binData.conversations[sessionKey]?.metadata?.demoRequested || false
  }
};

// Update stats
binData.stats.totalSessions = Object.keys(binData.conversations).length;
binData.stats.lastUpdated = DateTime.now().toISO();

return { json: { updatedBinData: binData } };

