// Parse AI intent classification and create routing logic
const aiResponse = $json.choices[0].message.content;
let classification;

try {
  classification = JSON.parse(aiResponse);
} catch (e) {
  // Fallback classification if parsing fails
  classification = {
    primary_intent: 'general_conversation',
    lead_quality: 'cold',
    urgency_level: 'exploring',
    confidence_score: 0.3,
    next_best_action: 'continue_conversation'
  };
}

// SIMPLIFIED ROUTING: Any business intent is a lead
const isHighIntentLead = 
  classification.primary_intent === 'demo_request' ||
  classification.primary_intent === 'pricing_inquiry' ||
  classification.primary_intent === 'contact_provided' ||
  classification.primary_intent === 'lead_qualification' ||
  classification.primary_intent === 'scheduling';

// DEBUG: Always log the classification
console.log('AI Classification:', JSON.stringify(classification));
console.log('Is High Intent Lead:', isHighIntentLead);

// Return classification data with routing flags
return {
  json: {
    ...classification,
    // Routing flags
    isHighIntentLead: Boolean(isHighIntentLead),
    useLeadFlow: isHighIntentLead,
    debugInfo: {
      originalIntent: classification.primary_intent,
      leadQuality: classification.lead_quality,
      routingDecision: isHighIntentLead ? 'LEAD_PATH' : 'STANDARD_PATH'
    }
  }
};

// Process calendar availability and suggest demo time slots
const schedules = $json.value || [];
const contactInfo = $node['Extract Contact Details'].json;

// Process availability data
let availableSlots = [];

if (schedules.length > 0) {
  const schedule = schedules[0];
  const availabilityView = schedule.availabilityView || '';
  const workingHours = schedule.workingHours || {};

  // Parse availability view (0 = Free, 2 = Busy)
  // Create time slots for the next 7 days during working hours
  const startDate = new Date();
  startDate.setDate(startDate.getDate() + 1); // Start tomorrow

  for (let day = 0; day < 7; day++) {
    const currentDate = new Date(startDate);
    currentDate.setDate(startDate.getDate() + day);

    // Skip weekends (assuming Monday-Friday work schedule)
    const dayOfWeek = currentDate.getDay();
    if (dayOfWeek === 0 || dayOfWeek === 6) continue;

    // Add morning and afternoon slots
    const morningSlot = new Date(currentDate);
    morningSlot.setHours(10, 0, 0, 0); // 10 AM

    const afternoonSlot = new Date(currentDate);
    afternoonSlot.setHours(14, 0, 0, 0); // 2 PM

    availableSlots.push({
      datetime: morningSlot.toISOString(),
      formatted: morningSlot.toLocaleDateString('en-US', { 
        weekday: 'long', 
        month: 'long', 
        day: 'numeric',
        hour: 'numeric',
        minute: '2-digit'
      }),
      timeSlot: 'morning',
      available: true
    });

    availableSlots.push({
      datetime: afternoonSlot.toISOString(),
      formatted: afternoonSlot.toLocaleDateString('en-US', { 
        weekday: 'long', 
        month: 'long', 
        day: 'numeric',
        hour: 'numeric',
        minute: '2-digit'
      }),
      timeSlot: 'afternoon',
      available: true
    });
  }
}

// Take the first 3 available slots
availableSlots = availableSlots.slice(0, 3);

// Generate response text
const slotsText = availableSlots.map((slot, index) => 
  `${index + 1}. ${slot.formatted}`
).join('\n');

const responseText = availableSlots.length > 0 
  ? `Perfect! I found these available demo times:\n\n${slotsText}\n\nWhich option works best for you?`
  : 'Let me check our calendar and get back to you with available demo times.';

return {
  json: {
    availableSlots: availableSlots,
    responseText: responseText,
    hasAvailability: availableSlots.length > 0,
    contactInfo: contactInfo
  }
};

// Enhanced appointment detection logic
const slots = $json.availableSlots || [];
const contact = $json.contactInfo || {};
const userMessage = $node['Extract Message Data'].json.message || '';

console.log('APPOINTMENT PREP - User message:', userMessage);
console.log('APPOINTMENT PREP - Available slots:', slots.length);
console.log('APPOINTMENT PREP - Contact info:', JSON.stringify({hasEmail: contact.hasEmail, hasPhone: contact.hasPhone, firstName: contact.firstName}));

// Comprehensive time selection patterns
const timeSelectionPatterns = [
  // Explicit option selection
  /(?:first|1st|one|option\s*1)/i,
  /(?:second|2nd|two|option\s*2)/i, 
  /(?:third|3rd|three|option\s*3)/i,

  // Specific day/time combinations
  /friday.*(?:at\s*)?10.*am/i,
  /friday.*(?:at\s*)?10:00/i,
  /friday.*(?:at\s*)?2.*pm/i, 
  /friday.*(?:at\s*)?2:00/i,
  /monday.*(?:at\s*)?10.*am/i,
  /monday.*(?:at\s*)?10:00/i,

  // Scheduling commitment phrases
  /(?:I'll take|I will take)/i,
  /(?:let's book|let's schedule|book that|schedule that)/i,
  /(?:schedule.*(?:me|that|this|it).*for)/i,
  /(?:book.*(?:me|that|this|it))/i,
  /(?:that works|sounds good|looks good)/i,
  /(?:perfect|great).*(?:time|slot)/i,

  // Selection phrases
  /(?:take.*(?:first|that|this))/i,
  /(?:works.*(?:better|good|fine|for me))/i,
  /(?:prefer.*(?:that|this))/i,
  /(?:choose.*(?:that|this|first))/i,

  // Confirmation phrases
  /(?:yes.*(?:book|schedule|that|please))/i,
  /(?:confirm.*(?:appointment|meeting|that))/i,
  /(?:go ahead|let's do it)/i
];

// Test each pattern and log detailed results
console.log('APPOINTMENT PREP - Testing', timeSelectionPatterns.length, 'patterns against:', JSON.stringify(userMessage));
let matchedPatterns = [];
timeSelectionPatterns.forEach((pattern, index) => {
  const matches = pattern.test(userMessage);
  if (matches) {
    matchedPatterns.push(`Pattern ${index}: ${pattern.toString()}`);
    console.log(`âœ… Pattern ${index} MATCHED: ${pattern.toString()}`);
  } else {
    console.log(`âŒ Pattern ${index} no match: ${pattern.toString()}`);
  }
});

const hasTimeSelection = matchedPatterns.length > 0;
console.log('APPOINTMENT PREP - Time selection detected:', hasTimeSelection, '- Matched patterns:', matchedPatterns.length);
console.log('APPOINTMENT PREP - Contact check - hasEmail:', contact.hasEmail, 'hasPhone:', contact.hasPhone);
console.log('APPOINTMENT PREP - All conditions - hasTimeSelection:', hasTimeSelection, 'slots.length > 0:', slots.length > 0, 'hasContact:', (contact.hasEmail || contact.hasPhone));

if (hasTimeSelection && slots.length > 0 && (contact.hasEmail || contact.hasPhone)) {
  // SCENARIO: User selected a specific time - CREATE APPOINTMENT
  const selectedSlot = slots[0];
  const startTime = new Date(selectedSlot.datetime);
  const endTime = new Date(startTime.getTime() + 30 * 60000);

  const appointmentData = {
    subject: 'AI Automation Demo - ' + (contact.firstName || 'Prospect'),
    body: {
      contentType: 'HTML', 
      content: 'Demo call with ' + (contact.firstName || 'Prospect') + ' ' + (contact.lastName || '') + '\nEmail: ' + (contact.email || 'Not provided') + '\nPhone: ' + (contact.phone || 'Not provided') + '\n\nDiscuss AI automation solutions and $58K+ savings potential.'
    },
    start: {
      dateTime: startTime.toISOString(),
      timeZone: 'UTC'
    },
    end: {
      dateTime: endTime.toISOString(), 
      timeZone: 'UTC'
    },
    attendees: contact.email ? [{
      emailAddress: {
        address: contact.email,
        name: (contact.firstName || 'Prospect') + ' ' + (contact.lastName || '')
      }
    }] : [],
    isOnlineMeeting: true,
    onlineMeetingProvider: 'teamsForBusiness',
    createAppointment: true
  };

  console.log('ðŸŽ¯ APPOINTMENT PREP - CREATING APPOINTMENT for:', selectedSlot.formatted, 'with contact:', contact.firstName || 'Prospect');
  return { json: appointmentData };
} else if (slots.length > 0 && (contact.hasEmail || contact.hasPhone) && !hasTimeSelection) {
  // SCENARIO: User has contact info, we have slots, but no specific time selected - SHOW AVAILABILITY ONLY
  console.log('ðŸ“… APPOINTMENT PREP - SHOWING AVAILABILITY (no specific time selected yet)');
  return { json: { 
    showAvailability: true,
    availableSlots: slots,
    contactInfo: contact,
    skipAppointment: true
  }};
} else {
  // SCENARIO: Don\'t show calendar flow at all - insufficient data
  console.log('âŒ APPOINTMENT PREP - NO CALENDAR FLOW - Reasons:');
  console.log('   - hasTimeSelection:', hasTimeSelection);
  console.log('   - slots.length > 0:', slots.length > 0);
  console.log('   - hasEmail || hasPhone:', (contact.hasEmail || contact.hasPhone));
  return { json: { 
    skipAppointment: true, 
    skipCalendar: true,
    debugInfo: { hasTimeSelection, slotsCount: slots.length, hasContact: (contact.hasEmail || contact.hasPhone), matchedPatterns, userMessage } 
  }};
}

// Smart response handler - handles both simple responses and full lead processing
const sessionId = $node['Extract Message Data'].json.sessionId || 'unknown';
const classification = $node['Process Intent Classification'].json;
const aiResponse = $node['Generate Unified Response'].json.choices[0].message.content;

// Check if we have processed calendar availability
let calendarData = null;
let availableSlots = [];
let hasCalendarData = false;

try {
  calendarData = $node['Process Available Times']?.json;
  if (calendarData) {
    availableSlots = calendarData.availableSlots || [];
    hasCalendarData = calendarData.hasAvailability || false;
  }
} catch (e) {
  // No calendar data available
}

// Check if we have contact information
let contactInfo = null;
let contactProcessed = false;

try {
  contactInfo = $node['Extract Contact Details']?.json;
  if (contactInfo) {
    contactProcessed = contactInfo.hasEmail || contactInfo.hasPhone || contactInfo.hasName;
  }
} catch (e) {
  // No contact data available - use fallback from unified processing
  try {
    const unifiedData = $node['Process Contact & Respond']?.json;
    if (unifiedData?.contactInfo) {
      contactInfo = unifiedData.contactInfo;
      contactProcessed = unifiedData.contactProcessed;
    }
  } catch (e2) {
    // No contact data at all
  }
}

// Determine lead status
const isLead = ((classification.primary_intent === 'demo_request' || 
               classification.primary_intent === 'contact_provided' || 
               classification.primary_intent === 'pricing_inquiry') &&
               (classification.lead_quality === 'hot' || classification.lead_quality === 'warm')) || 
               contactProcessed;

// Generate appropriate quick actions
let quickActions = [];

if (hasCalendarData && availableSlots.length > 0) {
  // Calendar availability found - show time slot options
  quickActions = [
    { text: 'ðŸ“… ' + (availableSlots[0]?.formatted || 'Option 1'), message: 'I\'ll take the first available time slot' },
    { text: 'ðŸ“… ' + (availableSlots[1]?.formatted || 'Option 2'), message: 'The second time slot works better' },
    { text: 'ðŸ“… ' + (availableSlots[2]?.formatted || 'Option 3'), message: 'I prefer the third time slot' },
    { text: 'ðŸ”„ Different Times', message: 'Do you have any different times available?' }
  ];
} else if (contactProcessed) {
  // Contact info processed - show scheduling options
  quickActions = [
    { text: 'ðŸ“… Schedule Now', message: 'Let\'s schedule the demo for this week' },
    { text: 'ðŸ“ž Call Me', message: 'Please have someone call me' },
    { text: 'ðŸ’¬ More Questions', message: 'I have a few more questions first' }
  ];
} else if (isLead) {
  // Lead intent but no contact info - ask for details
  quickActions = [
    { text: 'âœ… Share Contact', message: 'My name is John Smith, email john@company.com, phone 555-123-4567' },
    { text: 'ðŸ“… Schedule Demo', message: 'I\'d like to schedule a demo this week' },
    { text: 'ðŸ’° Pricing Info', message: 'What does this cost?' }
  ];
} else {
  // General conversation
  quickActions = [
    { text: 'ðŸ“… Schedule Demo', message: 'I\'d like to schedule a demo' },
    { text: 'ðŸ¤– AI Solutions', message: 'Tell me about your AI solutions' },
    { text: 'ðŸ’° Pricing Info', message: 'What does this cost?' },
    { text: 'ðŸ“ž Request Call', message: 'Can someone call me?' }
  ];
}

// Check if appointment was created and update response accordingly
let appointmentCreated = false;
let appointmentDetails = null;
try {
  const appointmentData = $node['Create Calendar Appointment']?.json;
  if (appointmentData && appointmentData.id) {
    appointmentCreated = true;
    appointmentDetails = {
      id: appointmentData.id,
      subject: appointmentData.subject,
      startTime: appointmentData.start?.dateTime,
      webLink: appointmentData.webLink
    };
  }
} catch (e) {
  // No appointment created
}

// Use calendar response if available, otherwise use AI response
let finalResponse = aiResponse;
if (appointmentCreated && appointmentDetails) {
  const appointmentTime = new Date(appointmentDetails.startTime).toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric', hour: 'numeric', minute: '2-digit' });
  finalResponse = `Perfect! I\'ve scheduled your AI Automation demo for ${appointmentTime}. You\'ll receive a calendar invitation with the meeting link shortly. Looking forward to showing you how our system can save your business $58K+ annually!`;
} else if (hasCalendarData && calendarData.responseText) {
  finalResponse = calendarData.responseText;
}

// Build comprehensive response
return {
  json: {
    response: finalResponse,
    sessionId: sessionId,
    timestamp: new Date().toISOString(),
    status: 'success',
    isLead: isLead,
    contactProcessed: contactProcessed,
    calendarProcessed: hasCalendarData,
    contactInfo: contactInfo,
    availableSlots: availableSlots,
    classification: {
      intent: classification.primary_intent || 'unknown',
      quality: classification.lead_quality || 'unknown',
      confidence: classification.confidence_score || 0
    },
    quickActions: quickActions,
    conversationContext: 'unified_processing',
    debugInfo: {
      hasCalendarData: hasCalendarData,
      contactProcessed: contactProcessed,
      availableSlots: availableSlots.length,
      processingPath: hasCalendarData ? 'FULL_LEAD_PROCESSING' : contactProcessed ? 'CONTACT_EXTRACTED' : 'SIMPLE_RESPONSE'
    }
  }
};

// Rebuild the response structure from Build Final Response data
const originalResponse = $node['Build Final Response'].json;

// Return the original response structure
return {
  json: originalResponse
};
